import React, {useEffect, useState } from 'react';
import { ReactDOM } from 'react-dom';


const BASE_URL = 'https://fitnesstrac-kr.herokuapp.com/api'

const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const [isLoggedIn, setIsLoggedIn] = useState(null)

    useEffect(() => {
        if (localStorage.getItem("token")) {
            setIsLoggedIn(true)
        } else{
            setIsLoggedIn(false);
        }
    },[]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const LoginUser = async (username, password) => {
            try {
                const response = await fetch(`${BASE_URL}/users/login`,{
                    method: "POST",
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        username: username,
                        password: password,
                    })
                })
                console.log('response:', response)

                if(response) {
                    const {token} = await response.json();
                    console.log("token =", token)
                }
            } catch (error) {
                console.error(error)
            }
        }
        LoginUser(username, password)
        setUsername('');
        setPassword('');
    }

    return (
        <div className='"Login-Form'>
            <form onSubmit={handleSubmit}>
                <h3> Sign On to Fitness Tracker </h3>
                <label> Username (required)</label><br></br>
                <input type='text' placeholder='Enter Username' value= {username} onChange={(event) => setUsername(event.target.value)}>
                </input>
                <br></br>
                <br></br>
                <label>Password (required)</label><br></br>
                <input type='text' placeholder='Enter Password' value= {password} onChange={(event) => setPassword(event.target.value)}>
                </input>
                <br></br>
                <br></br>
                <button type='submit'>
                    Sign On
                </button>
            </form>
        </div>



    )
}
export default Login;
