import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { BASE_URL } from '../..';


const LoginRequest = async (username, password) => {

    if(!username){
        window.alert(`Please Enter an Email Address`);
        return false;
    }

    else if(!password){
        window.alert(`Please Enter a Password`);
        return false;
    }

    else {
        const request = await fetch(`${BASE_URL}/users/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                email: username,
                password: password
            })
        });

        const response = await request.json();

        console.log(response)

        if(response.responseStatus === "Password Does Not Match"){
            window.alert(`Incorrect Password. Please Try Again.`);
            return false;
        }
        else if(response.responseStatus === "No User"){
            window.alert(`That username does not exist`);
            return false;
        }
        else if(response.responseStatus === "Unknown Error"){
            window.alert(`Unknown Error in /login route`);
            return false;
        }
        else if(response.responseStatus === "Passwords Match"){
            if (response.error) {
                window.alert(`Token Error`);
                return false;
            } 
            else if (response) { 
                localStorage.setItem("userToken", response.token);
                return true;
            };
        };
    };
};

function Login() {

    const navigate = useNavigate();

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();

        LoginRequest(username, password)
        .then(function(response){
            if(response){
                // Sends user to homepage
                navigate('/');
                window.location.reload(); 
            }
            else {
                setPassword('');
            }
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text"
                className="username"
                value={username}
                placeholder="Enter Username"
                onChange={(event) => setUsername(event.target.value)}>
            </input>

            <input 
                type="text"
                className="password"
                value={password}
                placeholder="Enter Password"
                onChange={(event) => setPassword(event.target.value)}>
            </input>

            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
