import React, { useEffect,useState } from 'react';
import { Link } from 'react-router-dom';
// import ReactDOM from 'react-dom';

const BASE_URL = 'https://fitnesstrac-kr.herokuapp.com/api';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const [isLoggedIn, setIsLoggedIn] = useState(null)

    useEffect(() => {
        if (localStorage.getItem('token')) {
            setIsLoggedIn(true)
        } else {
            setIsLoggedIn(false);
        }
    },[])

    const handleSubmit = async (event) => {

        event.preventDefault();

        setIsLoggedIn(true)

        const response = await fetch(`${BASE_URL}/users/login`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user: {
                    username: username,
                    password: password,
                }
            })
        })

        if (response) {

        const { data: { token } }= await response.json();
        // console.log('This is our post request data', data)

        localStorage.setItem("token", token);
        // console.log('welcome to the local storage', localStorage)

        setUsername('');
        setPassword('');
        }

    }


    return (
        <div className="postForm">
            {
                !isLoggedIn

                ?<><form onSubmit={handleSubmit}>
                <label>Username</label>
                <input type="text" placeholder="New Username" value={username} onChange={(event) => setUsername(event.target.value)}></input><br></br>

                <label>Password </label>
                <input type="text" placeholder="New Password" value={password} onChange={(event) => setPassword(event.target.value)}></input><br></br>

                <button type="submit" className="button">Login</button>
                </form></>

                : null
            }
        </div>
    )
}

export default Login;
