import React, {useState} from 'react';
// import { useNavigate } from 'react-router-dom';



const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api'; 

const registerRequest = async (username, password) => {

    // Send POST register request
    const request = await fetch(`${BASE_URL}/users/register`, {
        mode: "cors",
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            email: username,
            password: password
        })
    });

    // Await reponse
    const response = await request.json();

    // Logic Validation
    if(response.responseStatus === "No Email"){
        window.alert(`Please Enter an Email Address`);
        return false;
    }
    else if(response.responseStatus === "No Password"){
        window.alert(`Please Enter a Password`);
        return false;
    }
    else if(response.responseStatus === "Invalid Email"){
        window.alert(`"${username}" is not a valid email. Please enter a valid email.`);
        return false;
    }
    else if(response.responseStatus === "Non-Unique User"){
        window.alert(`"${username}" already exiests. Please enter a unique email address`);
        return false;
    }
    else if(response.responseStatus === "Success"){
        // Set Token
        if (response.error) {
            window.alert(`Token Error`);
            return false;
        } 
        else if (response) { 
            localStorage.setItem("userToken", response.token);
            return true;
        };
    }
    else{
        window.alert(`Unknown registerRequest error`);
    };

};


function RegisterPage() {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    // const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        registerRequest(username, password)
        .then(function(response){
            if(response){
                setUsername('');
                setPassword('');
                // Sends user to homepage
                navigate('/');
                window.location.reload();
            }
            else {
                setUsername('');
                setPassword('');
            }

        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text"
                className="username"
                value={username}
                placeholder="Enter Username"
                onChange={(event) => setUsername(event.target.value)}>
            </input>

            <input 
                type="text"
                className="password"
                value={password}
                placeholder="Enter Password"
                onChange={(event) => setPassword(event.target.value)}>
            </input>

            <button type="submit">Register</button>
        </form>
    );
};

export default RegisterPage;
